// frontend/src/components/MarsRoverPhotos.js
import React, { useState } from 'react';
import axios from 'axios';
import {
    Button,
    Container,
    TextField,
    Typography,
    Card,
    CardMedia,
    CardContent,
    Grid,
} from '@mui/material';
import { motion } from 'framer-motion';

const MarsRoverPhotos = () => {
    const [sol, setSol] = useState(1000); // Default sol
    const [photos, setPhotos] = useState([]);

    const fetchMarsPhotos = async () => {
        try {
            const response = await axios.get(`http://localhost:5004/mars-photos?sol=${sol}`);
            setPhotos(response.data.photos);
        } catch (error) {
            console.error('Error fetching Mars photos:', error);
        }
    };

    return (
        <Container sx={{ textAlign: 'center', padding: '40px' }}>
            <Typography variant="h4" gutterBottom>Mars Rover Photos</Typography>
            <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '20px' }}>
                <TextField
                    type="number"
                    value={sol}
                    onChange={(e) => setSol(e.target.value)}
                    label="Sol"
                    sx={{ marginRight: '25px', width: '200px' }}
                />
                <Button variant="contained" color="primary" onClick={fetchMarsPhotos}>
                    Fetch Mars Photos
                </Button>
            </div>

            <Grid container spacing={2}>
                {photos.map((photo) => (
                    <Grid item xs={12} sm={6} md={4} key={photo.id}>
                        <motion.div
                            initial={{ opacity: 0, y: 20 }} // Slide in from below
                            animate={{ opacity: 1, y: 0 }} // Slide to position
                            transition={{ duration: 0.5 }}
                        >
                            <Card sx={{ marginTop: '20px' }}>
                                <CardMedia
                                    component="img"
                                    height="200"
                                    image={photo.img_src}
                                    alt={`Mars Rover Photo ${photo.id}`}
                                />
                                <CardContent>
                                    <Typography variant="h6">Rover: {photo.rover.name}</Typography>
                                    <Typography variant="subtitle1">Date: {photo.earth_date}</Typography>
                                </CardContent>
                            </Card>
                        </motion.div>
                    </Grid>
                ))}
            </Grid>
        </Container>
    );
};

export default MarsRoverPhotos;
