// frontend/src/components/Apod.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import {
    Button,
    Container,
    TextField,
    Typography,
    Card,
    CardMedia,
    CardContent,
    Snackbar,
} from '@mui/material';
import { motion } from 'framer-motion';

const Apod = () => {
    const [apodData, setApodData] = useState(null);
    const [date, setDate] = useState('');
    const [error, setError] = useState('');

    const fetchApodData = async (date) => {
        setError('');
        const today = new Date().toISOString().split('T')[0];

        if (date > today) {
            setError('Please select a date that is today or in the past.');
            return;
        }

        try {
            const response = await axios.get(`http://localhost:5004/apod?date=${date}`);
            setApodData(response.data);
        } catch (err) {
            setError('Error fetching APOD data.');
        }
    };

    const handleDateChange = (e) => {
        setDate(e.target.value);
    };

    const handleFetchClick = () => {
        fetchApodData(date);
    };

    useEffect(() => {
        const today = new Date().toISOString().split('T')[0];
        fetchApodData(today);
    }, []);

    const today = new Date().toISOString().split('T')[0];

    return (
        <Container sx={{ textAlign: 'center', padding: '40px' }}>
            <Typography variant="h3" gutterBottom>Astronomy Picture of the Day</Typography>
            <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '20px' }}>
                <TextField
                    type="date"
                    value={date}
                    onChange={handleDateChange}
                    inputProps={{ max: today }} // Disable future dates
                    sx={{ marginRight: '10px', width: '200px' }}
                />
                <Button variant="contained" color="primary" onClick={handleFetchClick}>
                    Fetch for Selected Date
                </Button>
            </div>

            {error && (
                <Snackbar
                    open={Boolean(error)}
                    autoHideDuration={6000}
                    message={error}
                    onClose={() => setError('')}
                />
            )}

            {apodData && (
                <motion.div
                    initial={{ opacity: 0, y: -50 }} // Slide in from above
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.5 }}
                >
                    <Card sx={{ marginTop: '20px' }}>
                        <CardMedia
                            component="img"
                            height="400"
                            image={apodData.url}
                            alt={apodData.title}
                        />
                        <CardContent>
                            <Typography variant="h5">{apodData.title}</Typography>
                            <Typography variant="subtitle1">{apodData.date}</Typography>
                            <Typography variant="body2">{apodData.explanation}</Typography>
                            {apodData.hdurl && (
                                <Button variant="outlined" href={apodData.hdurl} target="_blank" rel="noopener noreferrer">
                                    <br/>View HD Image
                                </Button>
                            )}
                        </CardContent>
                    </Card>
                </motion.div>
            )}
        </Container>
    );
};

export default Apod;
