import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, TextField, Container, Typography, Grid, Card, CardMedia, MenuItem, Select, InputLabel, FormControl } from '@mui/material';
import styled from '@emotion/styled';

const StyledTextField = styled(TextField)`
    .MuiInputLabel-root {
        position: absolute;
        transform: translate(14px, -6px) scale(0.75);
    }
`;
const StyledInputLabel = styled(InputLabel)`
    position: absolute;
    transform: translate(14px, -6px) scale(0.75);
`;

const StyledSelect = styled(Select)`
    & .MuiInputLabel-root {
        position: absolute;
        transform: translate(14px, -6px) scale(0.75);
    }
`;

const MarsRoverPhotos = () => {
    const [sol, setSol] = useState(null); // Set initial state to null
    const [camera, setCamera] = useState(null); // Set initial state to null
    const [earthDate, setEarthDate] = useState(''); // Keep it empty for date
    const [photos, setPhotos] = useState([]);
    const [error, setError] = useState('');
    const [maxDate, setMaxDate] = useState('');

    const cameras = [
        { value: 'fhaz', label: 'Front Hazard Avoidance Camera' },
        { value: 'rhaz', label: 'Rear Hazard Avoidance Camera' },
        { value: 'mast', label: 'Mast Camera' },
        { value: 'chemcam', label: 'Chemistry and Camera Complex' },
        { value: 'mahli', label: 'Mars Hand Lens Imager' },
        { value: 'mardi', label: 'Mars Descent Imager' },
        { value: 'navcam', label: 'Navigation Camera' },
    ];

    useEffect(() => {
        // Set maxDate to today when the component mounts
        const today = new Date().toISOString().split('T')[0];
        setMaxDate(today);
    }, []);

const delay = (ms) => new Promise((resolve) => setTimeout(resolve, ms));


const fetchMarsPhotos = async () => {
    console.log(`Fetching Mars photos for Sol: ${sol}, Camera: ${camera}, Earth Date: ${earthDate}`);

    try {
        // Check if both Earth date and camera are selected
        if (earthDate && camera) {
            setError('Please select either Sol and Camera, or Earth Date. Not both.');
            return;
        }

        // Check if neither Sol nor Earth date is provided
        if (!earthDate && !sol) {
            setError('Please provide either a Sol value or an Earth date.');
            return;
        }

        // Use let for camera so we can reassign it
        let effectiveCamera = camera; // This will hold the camera value to use in the request

        // If Sol is provided but no camera is selected, set effectiveCamera to an empty string for "all cameras"
        if (sol && !effectiveCamera) {
            effectiveCamera = ''; // Default to all cameras when a Sol value is provided
        }

        let url = '';

        if (earthDate) {
            url = `https://api.nasa.gov/mars-photos/api/v1/rovers/curiosity/photos?earth_date=${earthDate}&api_key=DEMO_KEY`;
        } else if (sol) {
            // Construct the URL for Sol and handle the camera selection
            url = `http://localhost:5006/mars-photos?sol=${sol}`;
            if (effectiveCamera) {
                url += `&camera=${effectiveCamera}`; // Only add the camera if it's selected
            }
        }

        console.log('API Request URL:', url);

        // Add a delay to prevent hitting the rate limit
        await delay(1000); // Wait for 1 second

        const response = await axios.get(url);
        console.log('Response from Mars API:', response.data);

        if (response.data.photos && response.data.photos.length > 0) {
            setPhotos(response.data.photos);
            setError('');
        } else {
            setError(`No photos available for the specified criteria.`);
            setPhotos([]);
        }
    } catch (err) {
        setError('Error fetching Mars photos: ' + err.message);
        if (err.response && err.response.status === 429) {
            // Specific handling for rate limiting
            console.error('Rate limit exceeded. Please try again later.');
        }
        // Additional handling for server errors (500)
        if (err.response && err.response.status === 500) {
            console.error('Server error. Please try again later.');
        }
    }
};


    return (
        <Container sx={{ padding: '40px' }}>
            <Typography variant="h3" style={{ display: 'flex', justifyContent: 'center', marginBottom: '20px' }} gutterBottom>Mars Rover Photos</Typography>
            <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '20px' }}>
                <TextField
                    label="Sol"
                    type="number"
                    value={sol || ''} // Display empty string if null
                    onChange={(e) => setSol(e.target.value ? parseInt(e.target.value) : null)} // Set to null if input is empty
                    sx={{ marginRight: '10px', width: '100px' }}
                />
               <FormControl sx={{ marginRight: '10px', width: '200px' }}>
                   <StyledTextField
                       id="earth-date"
                       type="date"
                       label="Earth Date"
                       value={earthDate}
                       onChange={(e) => setEarthDate(e.target.value)}
                       InputLabelProps={{
                           shrink: true,
                       }}
                       inputProps={{
                           max: maxDate, // Disable future dates
                       }}
                   />
               </FormControl>
                <FormControl sx={{ marginRight: '10px', width: '200px' }}>
                    <StyledInputLabel id="camera-label">Camera</StyledInputLabel>
                    <StyledSelect
                        value={camera || ''} // Display empty string if null
                        onChange={(e) => setCamera(e.target.value || null)} // Set to null if no selection
                        displayEmpty
                        labelId="camera-label"
                    >
                        <MenuItem value="">
                            <em>All Cameras</em>
                        </MenuItem>
                        {cameras.map((cam) => (
                            <MenuItem key={cam.value} value={cam.value}>{cam.label}</MenuItem>
                        ))}
                    </StyledSelect>
                </FormControl>
                <Button variant="contained" color="primary" onClick={fetchMarsPhotos}>
                    Fetch Mars Photos
                </Button>
            </div>
            {error && <Typography color="error">{error}</Typography>}
            <Grid container spacing={3}>
                {photos.map((photo) => (
                    <Grid item xs={12} sm={6} md={4} key={photo.id}>
                        <Card>
                            <CardMedia
                                component="img"
                                alt={`Mars photo taken by ${photo.camera.full_name}`}
                                image={photo.img_src}
                                title={`Mars photo taken by ${photo.camera.full_name}`}
                            />
                        </Card>
                    </Grid>
                ))}
            </Grid>
        </Container>
    );
};

export default MarsRoverPhotos;
