// frontend/src/components/Apod.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Apod = () => {
    const [apodData, setApodData] = useState(null);
    const [date, setDate] = useState('');
    const [error, setError] = useState('');

    const fetchApodData = async (date) => {
        setError('');
        try {
            const response = await axios.get(`http://localhost:5002/apod?date=${date}`);
            setApodData(response.data);
        } catch (err) {
            setError('Error fetching APOD data.');
        }
    };

    const handleDateChange = (e) => {
        setDate(e.target.value);
    };

    const handleFetchClick = () => {
        fetchApodData(date);
    };

    useEffect(() => {
        // Fetch today's APOD data by default
        const today = new Date().toISOString().split('T')[0];
        fetchApodData(today);
    }, []);

    return (
        <div style={{ textAlign: 'center', padding: '20px' }}>
            <h1>Astronomy Picture of the Day</h1>
            <input
                type="date"
                value={date}
                onChange={handleDateChange}
                style={{ marginBottom: '20px' }}
            />
            <button onClick={handleFetchClick}>Fetch for Selected Date</button>

            {error && <p style={{ color: 'red' }}>{error}</p>}

            {apodData && (
                <div>
                    <h2>{apodData.title}</h2>
                    <h4>{apodData.date}</h4>
                    <img src={apodData.url} alt={apodData.title} style={{ maxWidth: '100%' }} />
                    <p>{apodData.explanation}</p>
                    {apodData.hdurl && <a href={apodData.hdurl} target="_blank" rel="noopener noreferrer">View HD Image</a>}
                </div>
            )}
        </div>
    );
};

export default Apod;
