import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Apod.css'; // Import your CSS file

const Apod = () => {
  const [apodData, setApodData] = useState(null);

  useEffect(() => {
    // Fetch APOD data from the backend
    axios.get('http://localhost:5002/apod')
      .then(response => {
        setApodData(response.data);
      })
      .catch(error => console.error('Error fetching APOD:', error));
  }, []);

  if (!apodData) return <div className="loading">Loading...</div>;

  return (
    <div className="apod-container">
      <h1 className="apod-title">{apodData.title}</h1>
      <p className="apod-date">{apodData.date}</p>
      {apodData.media_type === 'image' ? (
        <img src={apodData.url} alt={apodData.title} className="apod-image" />
      ) : (
        <iframe
          title="APOD Video"
          className="apod-video"
          src={apodData.url}
          allow="autoplay; encrypted-media"
          allowFullScreen
        />
      )}
      <div className="apod-explanation">
        <p>{apodData.explanation}</p>
      </div>
      {apodData.copyright && <p className="apod-copyright">Â© {apodData.copyright}</p>}
    </div>
  );
};

export default Apod;
