import React, { useState } from 'react';
import axios from 'axios';
import { Button, TextField, Container, Typography, Grid, Card, CardMedia, MenuItem, Select } from '@mui/material';

const MarsRoverPhotos = () => {
    const [sol, setSol] = useState(1000);
    const [camera, setCamera] = useState('');
    const [photos, setPhotos] = useState([]);
    const [error, setError] = useState('');

    const cameras = [
        { value: 'fhaz', label: 'Front Hazard Avoidance Camera' },
        { value: 'rhaz', label: 'Rear Hazard Avoidance Camera' },
        { value: 'mast', label: 'Mast Camera' },
        { value: 'chemcam', label: 'Chemistry and Camera Complex' },
        { value: 'mahli', label: 'Mars Hand Lens Imager' },
        { value: 'mardi', label: 'Mars Descent Imager' },
        { value: 'navcam', label: 'Navigation Camera' },
    ];

    const fetchMarsPhotos = async () => {
        // Debug log to check current values
        console.log(`Fetching Mars photos for Sol: ${sol}, Camera: ${camera}`);

        try {
            // Construct the API request URL
            const url = `http://localhost:5006/mars-photos?sol=${sol}${camera ? `&camera=${camera}` : ''}`;
            console.log('API Request URL:', url); // Log the API request URL

            const response = await axios.get(url);
            console.log('Response from Mars API:', response.data); // Log the response

            if (response.data.photos && response.data.photos.length > 0) {
                setPhotos(response.data.photos);
                setError('');
            } else {
                setError(`No photos available for Sol ${sol} with camera ${camera}.`);
                setPhotos([]);
            }
        } catch (err) {
            setError('Error fetching Mars photos: ' + err.message);
        }
    };

    return (
        <Container sx={{ padding: '40px' }}>
            <Typography variant="h3" style={{ display: 'flex', justifyContent: 'center', marginBottom: '20px' }} gutterBottom>Mars Rover Photos</Typography>
            <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '20px' }}>
                <TextField
                    label="Sol"
                    type="number"
                    value={sol}
                    onChange={(e) => setSol(e.target.value)}
                    sx={{ marginRight: '10px', width: '100px' }}
                />
                <Select
                    value={camera}
                    onChange={(e) => setCamera(e.target.value)}
                    displayEmpty
                    sx={{ marginRight: '10px', width: '200px' }}
                >
                    <MenuItem value="">
                        <em>All Cameras</em>
                    </MenuItem>
                    {cameras.map((cam) => (
                        <MenuItem key={cam.value} value={cam.value}>{cam.label}</MenuItem>
                    ))}
                </Select>
                <Button variant="contained" color="primary" onClick={fetchMarsPhotos}>
                    Fetch Mars Photos
                </Button>
            </div>
            {error && <Typography color="error">{error}</Typography>}
            <Grid container spacing={3}>
                {photos.map((photo) => (
                    <Grid item xs={12} sm={6} md={4} key={photo.id}>
                        <Card>
                            <CardMedia
                                component="img"
                                alt={`Mars photo taken by ${photo.camera.full_name}`}
                                image={photo.img_src}
                                title={`Mars photo taken by ${photo.camera.full_name}`}
                            />
                        </Card>
                    </Grid>
                ))}
            </Grid>
        </Container>
    );
};

export default MarsRoverPhotos;
