import React, { useState } from 'react';
import axios from 'axios';
import { Button, TextField, Container, Typography, Grid, Card, CardMedia } from '@mui/material';

const MarsRoverPhotos = () => {
    const [sol, setSol] = useState(1000);
    const [camera, setCamera] = useState('');
    const [page, setPage] = useState(1);
    const [photos, setPhotos] = useState([]);
    const [error, setError] = useState('');

    const fetchMarsPhotos = async () => {
        try {
            const response = await axios.get(`http://localhost:5006/mars-photos?sol=${sol}${camera ? `&camera=${camera}` : ''}${page ? `&page=${page}` : ''}`);
            setPhotos(response.data.photos);
            setError('');
        } catch (err) {
            setError('Error fetching Mars photos.');
            setPhotos([]); // Clear photos on error
        }
    };

    return (
        <Container sx={{ padding: '40px' }}>
            <Typography variant="h3" style={{ display: 'flex', justifyContent: 'center', marginBottom: '20px' }} gutterBottom>Mars Rover Photos</Typography>
            <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '20px' }}>
                <TextField
                    label="Sol"
                    type="number"
                    value={sol}
                    onChange={(e) => setSol(e.target.value)}
                    sx={{ marginRight: '10px', width: '200px' }}
                />
                <TextField
                    label="Camera (e.g., mast, navcam)"
                    value={camera}
                    onChange={(e) => setCamera(e.target.value)}
                    sx={{ marginRight: '10px', width: '200px' }}
                />
                <TextField
                    label="Page"
                    type="number"
                    value={page}
                    onChange={(e) => setPage(e.target.value)}
                    sx={{ marginRight: '10px', width: '200px' }}
                />
                <Button variant="contained" color="primary" onClick={fetchMarsPhotos}>
                    Fetch Mars Photos
                </Button>
            </div>
            {error && <Typography color="error">{error}</Typography>}
            <Grid container spacing={3}>
                {photos.map((photo) => (
                    <Grid item xs={12} sm={6} md={4} key={photo.id}>
                        <Card>
                            <CardMedia
                                component="img"
                                alt={`Mars photo taken by ${photo.camera.full_name}`}
                                image={photo.img_src}
                                title={`Mars photo taken by ${photo.camera.full_name}`}
                            />
                        </Card>
                    </Grid>
                ))}
            </Grid>
        </Container>
    );
};

export default MarsRoverPhotos;
