import React, { useEffect, useState } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './Apod.css';
import { ClipLoader } from 'react-spinners';  // Loading spinner

const Apod = () => {
  const [apodData, setApodData] = useState(null);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch APOD data when date changes
 useEffect(() => {
   const fetchApodData = async () => {
     setLoading(true);
     setError(null);
     const formattedDate = selectedDate.toISOString().split('T')[0];
     try {
       const response = await axios.get(`http://localhost:5002/apod?date=${formattedDate}&_=${new Date().getTime()}`);
       if (response.data.date === apodData.date) {
         setError('No new data available for the selected date.');
       } else {
         setApodData(response.data);
       }
     } catch (err) {
       setError('Error fetching APOD data');
     }
     setLoading(false);
   };

   fetchApodData();
 }, [selectedDate]);


  // Loading state
  if (loading) return <ClipLoader size={50} color="#123abc" loading={true} />;

  // Error state
  if (error) return <div className="error-message">{error}</div>;

  // Conditional rendering to ensure apodData is loaded before rendering the content
  return (
    <div className="apod-container">
      {apodData ? (
        <>
          <h1 className="apod-title">{apodData.title}</h1>
          <p className="apod-date">{apodData.date}</p>

          {/* Date Picker to select the APOD date */}
          <DatePicker
            selected={selectedDate}
            onChange={(date) => setSelectedDate(date)}
            dateFormat="yyyy-MM-dd"
            className="date-picker"
          />

          {/* Media (image or video) */}
          {apodData.media_type === 'image' ? (
            <img src={apodData.url} alt={apodData.title} className="apod-image" />
          ) : (
            <iframe
              title="APOD Video"
              className="apod-video"
              src={apodData.url}
              allow="autoplay; encrypted-media"
              allowFullScreen
            />
          )}

          {/* Explanation */}
          <div className="apod-explanation">
            <p>{apodData.explanation}</p>
          </div>

          {/* Copyright */}
          {apodData.copyright && <p className="apod-copyright">Â© {apodData.copyright}</p>}
        </>
      ) : (
        <p>Loading data...</p>
      )}
    </div>
  );
};

export default Apod;
